SELECT COUNT(*) FROM comments as c, postHistory as ph, badges as b, users as u WHERE u.Id = c.UserId AND u.Id = ph.UserId AND u.Id = b.UserId AND c.Score = 3 AND c.CreationDate >= '2010-09-29 18:48:43'::timestamp AND ph.PostHistoryTypeId = 4 AND ph.CreationDate >= '2011-03-30 10:25:50'::timestamp AND u.Reputation >= 2123 AND u.Reputation <= 16222 AND u.DownVotes <= 724 AND u.CreationDate >= '2012-10-27 10:37:05'::timestamp AND u.CreationDate <= '2013-09-22 02:18:41'::timestamp;