SELECT COUNT(*) FROM comments as c, postHistory as ph, badges as b, users as u WHERE u.Id = c.UserId AND u.Id = ph.UserId AND u.Id = b.UserId AND c.Score = 5 AND c.CreationDate >= '2009-06-19 17:40:20'::timestamp AND ph.PostHistoryTypeId = 4 AND ph.CreationDate >= '2013-09-11 05:46:27'::timestamp AND u.Reputation >= 15504 AND u.Reputation <= 63044 AND u.DownVotes <= 566 AND u.CreationDate >= '2013-01-20 07:26:50'::timestamp AND u.CreationDate <= '2014-06-14 13:55:12'::timestamp;