SELECT COUNT(*) FROM comments as c, postHistory as ph, badges as b, users as u WHERE u.Id = c.UserId AND u.Id = ph.UserId AND u.Id = b.UserId AND c.Score = 0 AND c.CreationDate >= '2014-02-21 07:50:54'::timestamp AND ph.PostHistoryTypeId = 9 AND ph.CreationDate >= '2012-08-07 15:55:08'::timestamp AND u.Reputation >= 1726 AND u.Reputation <= 43082 AND u.DownVotes <= 724 AND u.CreationDate >= '2011-05-31 02:36:57'::timestamp AND u.CreationDate <= '2013-11-26 13:55:51'::timestamp;