SELECT COUNT(*) FROM comments as c, postHistory as ph, badges as b, users as u WHERE u.Id = c.UserId AND u.Id = ph.UserId AND u.Id = b.UserId AND c.Score = 4 AND c.CreationDate >= '2010-05-17 17:30:59'::timestamp AND ph.PostHistoryTypeId = 1 AND ph.CreationDate >= '2011-03-16 14:47:22'::timestamp AND u.Reputation >= 15372 AND u.Reputation <= 45852 AND u.DownVotes <= 1850 AND u.CreationDate >= '2010-10-15 09:34:17'::timestamp AND u.CreationDate <= '2011-01-11 20:57:24'::timestamp;