SELECT COUNT(*) FROM comments as c, postHistory as ph, badges as b, users as u WHERE u.Id = c.UserId AND u.Id = ph.UserId AND u.Id = b.UserId AND c.Score = 13 AND c.CreationDate >= '2009-12-18 10:41:06'::timestamp AND ph.PostHistoryTypeId = 8 AND ph.CreationDate >= '2012-12-11 01:06:10'::timestamp AND u.Reputation >= 1572 AND u.Reputation <= 42238 AND u.DownVotes <= 728 AND u.CreationDate >= '2012-02-13 19:50:28'::timestamp AND u.CreationDate <= '2013-12-13 06:59:47'::timestamp;