SELECT COUNT(*) FROM postLinks as pl, posts as p, users as u, badges as b WHERE p.Id = pl.RelatedPostId AND u.Id = p.OwnerUserId AND u.Id = b.UserId AND pl.LinkTypeId = 3 AND p.Score >= 4 AND p.CommentCount <= 38 AND p.CreationDate >= '2013-08-15 03:47:12'::timestamp AND p.CreationDate <= '2014-09-01 19:46:43'::timestamp AND u.Views <= 20455 AND u.CreationDate >= '2013-12-18 22:39:17'::timestamp AND u.CreationDate <= '2014-01-21 06:57:05'::timestamp;