SELECT COUNT(*) FROM comments as c, posts as p, votes as v, badges as b, users as u WHERE u.Id =c.UserId AND c.UserId = p.OwnerUserId AND p.OwnerUserId = v.UserId AND v.UserId = b.UserId AND c.Score=5 AND p.Score>=37 AND p.Score<=60 AND p.CreationDate>='2011-01-11 13:25:49'::timestamp AND p.CreationDate<='2012-06-01 01:44:43'::timestamp AND v.BountyAmount<=297 AND b.Date<='2012-12-29 15:28:13'::timestamp AND u.DownVotes<=180 AND u.CreationDate>='2010-08-14 08:59:20'::timestamp AND u.CreationDate<='2013-03-08 18:28:08'::timestamp;