SELECT COUNT(*) FROM comments as c, posts as p, postHistory as ph, badges as b, users as u WHERE u.Id = c.UserId AND u.Id = p.OwnerUserId AND u.Id = ph.UserId AND u.Id = b.UserId AND c.CreationDate>='2011-01-13 07:42:37'::timestamp AND c.CreationDate<='2013-04-01 14:04:38'::timestamp AND p.Score>=52 AND p.ViewCount>=4178 AND p.ViewCount<=147818 AND ph.PostHistoryTypeId=2 AND b.Date>='2011-10-17 22:50:24'::timestamp AND u.Views>=988 AND u.Views<=7752;