SELECT COUNT(*) FROM comments as c, posts as p, postLinks as pl, votes as v, badges as b, users as u WHERE p.Id = c.PostId AND p.Id = pl.RelatedPostId AND p.Id = v.PostId AND u.Id = p.OwnerUserId AND u.Id = b.UserId AND c.Score=2 AND p.AnswerCount>=2 AND p.AnswerCount<=53 AND p.CreationDate<='2014-08-31 05:13:26'::timestamp AND pl.LinkTypeId=1 AND pl.CreationDate>='2011-12-07 12:14:16'::timestamp AND v.BountyAmount<=107 AND v.CreationDate>='2012-12-04 20:03:06'::timestamp AND v.CreationDate<='2013-02-01 06:29:49'::timestamp AND u.Views<=12902 AND u.CreationDate>='2011-07-16 12:04:59'::timestamp AND u.CreationDate<='2014-08-16 13:04:17'::timestamp;