SELECT COUNT(*) FROM comments as c, posts as p, postLinks as pl, postHistory as ph, votes as v, users as u WHERE p.Id = pl.PostId AND p.Id = ph.PostId AND p.Id = c.PostId AND u.Id = c.UserId AND u.Id = v.UserId AND c.CreationDate<='2014-07-31 08:47:34'::timestamp AND p.PostTypeId=1 AND p.Score=40 AND p.FavoriteCount<=97 AND pl.CreationDate>='2011-02-18 06:40:23'::timestamp AND pl.CreationDate<='2014-08-12 18:31:26'::timestamp AND ph.CreationDate>='2011-07-26 08:45:20'::timestamp AND ph.CreationDate<='2014-04-14 02:07:37'::timestamp AND v.CreationDate>='2013-06-13 08:53:40'::timestamp AND u.Reputation>=4886 AND u.DownVotes>=409;