SELECT COUNT(*) FROM comments as c, posts as p, postLinks as pl, postHistory as ph, votes as v, badges as b WHERE p.Id = c.PostId AND p.Id = pl.RelatedPostId AND p.Id = ph.PostId AND p.Id = v.PostId AND b.UserId = c.UserId AND c.Score=0 AND p.Score<=6 AND p.ViewCount<=135510 AND pl.LinkTypeId=1 AND v.CreationDate<='2011-08-10 21:20:24'::timestamp;