SELECT COUNT(*) FROM comments as c, posts as p, postLinks as pl, postHistory as ph, votes as v, badges as b WHERE p.Id = c.PostId AND p.Id = pl.RelatedPostId AND p.Id = ph.PostId AND p.Id = v.PostId AND b.UserId = c.UserId AND c.Score=10 AND p.Score<=101 AND p.ViewCount<=50985 AND pl.LinkTypeId=3 AND v.CreationDate<='2012-08-24 16:18:38'::timestamp;