SELECT COUNT(*) FROM comments as c, posts as p, postLinks as pl, postHistory as ph, votes as v, badges as b WHERE p.Id = c.PostId AND p.Id = pl.RelatedPostId AND p.Id = ph.PostId AND p.Id = v.PostId AND b.UserId = c.UserId AND c.CreationDate>='2011-06-09 00:27:53'::timestamp AND c.CreationDate<='2014-07-31 07:14:10'::timestamp AND p.Score<=155 AND p.ViewCount<=112904 AND p.AnswerCount>=43 AND p.AnswerCount<=131 AND p.CommentCount<=43 AND p.FavoriteCount>=15 AND v.VoteTypeId=15 AND v.CreationDate>='2010-12-01 11:06:16'::timestamp AND b.Date>='2013-05-09 21:00:30'::timestamp;