SELECT COUNT(*) FROM comments as c, posts as p, postLinks as pl, postHistory as ph, votes as v, badges as b WHERE p.Id = c.PostId AND p.Id = pl.RelatedPostId AND p.Id = ph.PostId AND p.Id = v.PostId AND b.UserId = c.UserId AND c.Score=20 AND p.Score<=112 AND ph.PostHistoryTypeId=1 AND b.Date<='2010-11-29 19:17:45'::timestamp;