SELECT COUNT(*) FROM comments as c, posts as p, postLinks as pl, postHistory as ph, votes as v, badges as b WHERE p.Id = c.PostId AND p.Id = pl.RelatedPostId AND p.Id = ph.PostId AND p.Id = v.PostId AND b.UserId = c.UserId AND c.Score=1 AND p.Score<=181 AND ph.PostHistoryTypeId=38 AND b.Date<='2012-07-09 23:57:43'::timestamp;