SELECT COUNT(*) FROM comments as c, posts as p, postHistory as ph, votes as v, badges as b, users as u WHERE u.Id = p.OwnerUserId AND u.Id = b.UserId AND p.Id = c.PostId AND p.Id = ph.PostId AND p.Id = v.PostId AND c.Score=0 AND c.CreationDate<='2012-08-30 04:44:09'::timestamp AND p.Score>=97 AND p.ViewCount>=47793 AND p.ViewCount<=80129 AND p.AnswerCount<=125 AND p.CommentCount>=0 AND p.CommentCount<=6 AND p.CreationDate>='2009-09-05 09:45:44'::timestamp AND u.Reputation<=12488 AND u.DownVotes<=13;