SELECT COUNT(*) FROM comments as c, posts as p, postHistory as ph, votes as v, badges as b, users as u WHERE u.Id = p.OwnerUserId AND u.Id = b.UserId AND p.Id = c.PostId AND p.Id = ph.PostId AND p.Id = v.PostId AND c.Score=0 AND c.CreationDate<='2011-11-21 02:37:59'::timestamp AND p.Score>=155 AND p.ViewCount>=43179 AND p.ViewCount<=139206 AND p.AnswerCount<=44 AND p.CommentCount>=2 AND p.CommentCount<=7 AND p.CreationDate>='2010-04-01 21:08:52'::timestamp AND u.Reputation<=55687 AND u.DownVotes<=673;