SELECT COUNT(*) FROM comments as c, posts as p, postHistory as ph, votes as v, badges as b, users as u WHERE u.Id = p.OwnerUserId AND u.Id = b.UserId AND p.Id = c.PostId AND p.Id = ph.PostId AND p.Id = v.PostId AND p.PostTypeId=1 AND p.ViewCount<=48733 AND p.CommentCount>=13 AND p.CommentCount<=21 AND ph.PostHistoryTypeId=6 AND v.VoteTypeId=2 AND u.Views<=7432 AND u.DownVotes>=151 AND u.DownVotes<=1800 AND u.UpVotes<=10132;