SELECT COUNT(*) FROM comments as c, posts as p, postHistory as ph, votes as v, badges as b, users as u WHERE u.Id = p.OwnerUserId AND u.Id = b.UserId AND p.Id = c.PostId AND p.Id = ph.PostId AND p.Id = v.PostId AND p.PostTypeId=1 AND p.ViewCount<=51517 AND p.CommentCount>=0 AND p.CommentCount<=40 AND ph.PostHistoryTypeId=5 AND v.VoteTypeId=5 AND u.Views<=13871 AND u.DownVotes>=139 AND u.DownVotes<=667 AND u.UpVotes<=1207;