SELECT COUNT(*) FROM comments as c, posts as p, votes as v, users as u WHERE u.Id = p.OwnerUserId AND p.Id = v.PostId AND p.Id = c.PostId AND c.Score=10 AND c.CreationDate<='2012-08-13 09:03:04'::timestamp AND p.Score>=52 AND p.Score<=84 AND p.CommentCount<=17 AND p.CreationDate<='2014-07-27 09:37:13'::timestamp AND v.CreationDate<='2012-07-03 21:57:15'::timestamp AND u.DownVotes>=16;