SELECT COUNT(*) FROM comments as c, posts as p, votes as v, users as u WHERE u.Id = p.OwnerUserId AND p.Id = v.PostId AND p.Id = c.PostId AND c.Score=2 AND c.CreationDate<='2011-06-26 04:40:41'::timestamp AND p.Score>=1 AND p.Score<=150 AND p.CommentCount<=22 AND p.CreationDate<='2014-02-02 10:33:25'::timestamp AND v.CreationDate<='2010-12-30 06:11:53'::timestamp AND u.DownVotes>=326;