SELECT COUNT(*) FROM comments as c, postHistory as ph, badges as b, users as u WHERE u.Id = b.UserId AND u.Id = ph.UserId AND u.Id = c.UserId AND ph.PostHistoryTypeId=1 AND ph.CreationDate<='2010-12-31 02:40:11'::timestamp AND b.Date<='2012-08-24 21:59:52'::timestamp AND u.Views>=10219 AND u.DownVotes>=510 AND u.UpVotes>=11224 AND u.UpVotes<=11299 AND u.CreationDate>='2010-07-30 13:40:23'::timestamp AND u.CreationDate<='2012-05-28 18:12:17'::timestamp;