SELECT COUNT(*) FROM comments as c, posts as p, postLinks as pl, postHistory as ph, users as u WHERE pl.RelatedPostId = p.Id AND u.Id= c.UserId AND c.PostId = p.Id AND ph.PostId = p.Id AND c.CreationDate>='2009-02-15 03:55:57'::timestamp AND c.CreationDate<='2013-01-28 19:52:45'::timestamp AND p.CommentCount>=7 AND p.CommentCount<=15 AND pl.LinkTypeId=3 AND ph.CreationDate>='2012-11-27 08:53:23'::timestamp AND u.Reputation>=34589 AND u.Reputation<=50816 AND u.DownVotes>=76 AND u.DownVotes<=1403;