SELECT COUNT(*) FROM comments as c, posts as p, postLinks as pl, postHistory as ph, users as u WHERE pl.RelatedPostId = p.Id AND u.Id= c.UserId AND c.PostId = p.Id AND ph.PostId = p.Id AND c.CreationDate>='2011-10-24 14:15:36'::timestamp AND c.CreationDate<='2014-04-29 22:46:56'::timestamp AND p.CommentCount>=15 AND p.CommentCount<=45 AND pl.LinkTypeId=3 AND ph.CreationDate>='2013-02-23 22:31:58'::timestamp AND u.Reputation>=35436 AND u.Reputation<=84458 AND u.DownVotes>=36 AND u.DownVotes<=187;