SELECT COUNT(*) FROM comments as c, posts as p, postHistory as ph, badges as b, users as u WHERE u.Id = ph.UserId AND u.Id = b.UserId AND u.Id = p.OwnerUserId AND u.Id = c.UserId AND c.Score=3 AND p.PostTypeId=1 AND p.ViewCount>=1019 AND p.ViewCount<=49554 AND p.FavoriteCount=24 AND p.CreationDate<='2011-09-17 05:55:34'::timestamp;