SELECT COUNT(*) FROM comments as c, posts as p, postLinks as pl, votes as v, badges as b, users as u WHERE u.Id = p.OwnerUserId AND p.Id = pl.RelatedPostId AND p.Id = v.PostId AND p.Id = c.PostId AND u.Id = b.UserId AND p.Score>=-1 AND p.Score<=170 AND pl.CreationDate<='2013-10-14 00:53:18'::timestamp AND v.CreationDate>='2012-06-08 00:51:42'::timestamp AND b.Date>='2010-12-07 18:49:27'::timestamp AND b.Date<='2011-03-12 19:46:04'::timestamp AND u.DownVotes>=747;