SELECT COUNT(*) FROM comments as c, posts as p, postLinks as pl, votes as v, badges as b, users as u WHERE u.Id = p.OwnerUserId AND p.Id = pl.RelatedPostId AND p.Id = v.PostId AND p.Id = c.PostId AND u.Id = b.UserId AND p.Score>=4 AND p.Score<=52 AND pl.CreationDate<='2013-06-30 17:23:25'::timestamp AND v.CreationDate>='2010-12-08 07:31:47'::timestamp AND b.Date>='2014-04-11 03:57:49'::timestamp AND b.Date<='2014-09-08 01:00:15'::timestamp AND u.DownVotes>=767;