SELECT COUNT(*) FROM comments as c, posts as p, postLinks as pl, postHistory as ph, votes as v, badges as b, users as u WHERE p.Id = pl.RelatedPostId AND b.UserId = u.Id AND c.UserId = u.Id AND p.Id = v.PostId AND p.Id = c.PostId AND p.Id = ph.PostId AND c.Score=2 AND p.ViewCount>=45542 AND p.AnswerCount<=102 AND p.CommentCount<=20 AND p.FavoriteCount>=75 AND pl.LinkTypeId=1 AND pl.CreationDate>='2013-12-15 22:11:51'::timestamp AND pl.CreationDate<='2014-04-16 07:01:44'::timestamp AND v.CreationDate>='2009-12-22 00:36:52'::timestamp AND v.CreationDate<='2011-04-27 02:10:15'::timestamp AND b.Date>='2014-04-15 05:01:53'::timestamp AND b.Date<='2014-08-15 01:50:44'::timestamp;