SELECT COUNT(*) FROM comments as c, posts as p, postLinks as pl, postHistory as ph, votes as v, badges as b, users as u WHERE p.Id = pl.RelatedPostId AND b.UserId = u.Id AND c.UserId = u.Id AND p.Id = v.PostId AND p.Id = c.PostId AND p.Id = ph.PostId AND p.Score<=81 AND p.CommentCount>=14 AND p.CreationDate>='2013-02-23 14:18:08'::timestamp AND p.CreationDate<='2013-12-04 20:50:26'::timestamp AND pl.LinkTypeId=3;