SELECT COUNT(*) FROM comments as c, posts as p, postLinks as pl, postHistory as ph, votes as v, badges as b, users as u WHERE p.Id = pl.RelatedPostId AND b.UserId = u.Id AND c.UserId = u.Id AND p.Id = v.PostId AND p.Id = c.PostId AND p.Id = ph.PostId AND p.Score<=64 AND p.CommentCount>=2 AND p.CreationDate>='2009-07-14 21:32:41'::timestamp AND p.CreationDate<='2012-08-31 12:03:47'::timestamp AND pl.LinkTypeId=1;