SELECT COUNT(*) FROM comments as c, posts as p, postLinks as pl, postHistory as ph, votes as v, badges as b, users as u WHERE p.Id = pl.RelatedPostId AND b.UserId = u.Id AND c.UserId = u.Id AND p.Id = v.PostId AND p.Id = c.PostId AND p.Id = ph.PostId AND p.Score<=170 AND p.CommentCount>=0 AND p.CreationDate>='2010-04-07 00:52:11'::timestamp AND p.CreationDate<='2011-01-12 11:18:13'::timestamp AND pl.LinkTypeId=1;