SELECT COUNT(*) FROM comments as c, posts as p, postLinks as pl, postHistory as ph, votes as v, badges as b, users as u WHERE p.Id = pl.RelatedPostId AND b.UserId = u.Id AND c.UserId = u.Id AND p.Id = v.PostId AND p.Id = c.PostId AND p.Id = ph.PostId AND c.Score=0 AND c.CreationDate>='2010-03-25 23:03:44'::timestamp AND p.PostTypeId=1 AND p.AnswerCount>=1 AND p.CommentCount>=13 AND p.CommentCount<=40 AND pl.CreationDate>='2012-12-03 21:40:08'::timestamp AND pl.CreationDate<='2013-02-20 09:14:42'::timestamp AND ph.CreationDate<='2011-05-03 08:14:30'::timestamp AND v.CreationDate>='2009-12-16 22:48:46'::timestamp AND v.CreationDate<='2012-05-20 17:02:59'::timestamp;