SELECT COUNT(*) FROM comments as c, posts as p, postHistory as ph, votes as v, badges as b, users as u WHERE u.Id = p.OwnerUserId AND p.Id = v.PostId AND p.Id = c.PostId AND u.Id = b.UserId AND p.Id = ph.PostId AND p.AnswerCount>=3 AND p.CommentCount>=5 AND b.Date<='2013-06-03 06:39:02'::timestamp AND u.Reputation>=3087 AND u.Reputation<=73326 AND u.DownVotes>=405;