SELECT COUNT(*) FROM comments as c, posts as p, postLinks as pl, postHistory as ph, votes as v, users as u WHERE u.Id = p.OwnerUserId AND p.Id = v.PostId AND p.Id = c.PostId AND p.Id = pl.PostId AND p.Id = ph.PostId AND c.CreationDate>='2013-03-01 18:46:25'::timestamp AND p.Score>=4 AND p.CommentCount<=40 AND p.CreationDate>='2012-06-27 11:08:17'::timestamp AND p.CreationDate<='2013-12-10 11:44:34'::timestamp AND pl.CreationDate<='2013-08-10 12:19:06'::timestamp AND ph.CreationDate>='2009-06-14 08:21:10'::timestamp AND u.DownVotes>=221 AND u.UpVotes>=2439;